  gtk_container_set_border_width (GTK_CONTAINER (txfileselection), 10);
  gtk_file_selection_hide_fileop_buttons (GTK_FILE_SELECTION (txfileselection));

  txfileselect_ok_button = GTK_FILE_SELECTION (txfileselection)->ok_button;
  gtk_widget_show (txfileselect_ok_button);
