  GtkWidget *macroconfigtext;
  GtkWidget *dialog_action_area2;
  GtkWidget *helpbutton1;
  GtkWidget *button1;
  GtkWidget *button2;
  GtkWidget *button3;

  macroconfigdialog = gtk_dialog_new ();
  gtk_window_set_resizable (GTK_WINDOW (macroconfigdialog), FALSE);

  dialog_vbox2 = GTK_DIALOG (macroconfigdialog)->vbox;
  gtk_widget_show (dialog_vbox2);

  vbox25 = gtk_vbox_new (FALSE, 0);
  gtk_widget_show (vbox25);
  gtk_box_pack_start (GTK_BOX (dialog_vbox2), vbox25, TRUE, TRUE, 4);
